<?php // Do not delete these lines	if ('comments.php' == basename($_SERVER['SCRIPT_FILENAME']))		die ('Please do not load this page directly. Thanks!');        if (!empty($post->post_password)) { // if there's a password            if ($_COOKIE['wp-postpass_' . COOKIEHASH] != $post->post_password) {  // and it doesn't match the cookie				?><p class="nocomments">This post is password protected. Enter the password to view comments.</p><p>  <?php				return;            }        }		/* This variable is for alternating comment background */		$oddcomment = 'alt';?>  <!-- You can start editing here. --><div class="maya2">  <?php if ($comments) : ?>  <h3 id="comments">    <?php comments_number('No Comments', 'One Comment', '% Comments' );?>  </h3>  <ol class="commentlist">    <?php foreach ($comments as $comment) : ?>    <li class="<?php echo $oddcomment; ?>" id="comment-<?php comment_ID() ?>">      <?php if(function_exists("get_avatar")) echo get_avatar( $comment, 32 ); ?>      <cite>      <?php comment_author_link() ?>      :</cite>      <?php if ($comment->comment_approved == '0') : ?>      Your comment is awaiting moderation.      <?php endif; ?>      <br />      <small class="commentmetadata"><a href="#comment-<?php comment_ID() ?>">      <?php comment_date('F jS, Y') ?>      <?php comment_time() ?>      </a></small>      <?php comment_text() ?>    </li>    <?php /* Changes every other comment to a different class */		if ('alt' == $oddcomment) $oddcomment = 'alttwo';		else $oddcomment = 'alt';	?>    <?php endforeach; /* end for each comment */ ?>  </ol>  <?php else : // this is displayed if there are no comments so far ?>  <?php if ('open' == $post->comment_status) : ?>  <!-- If comments are open, but there are no comments. -->  <?php else : // comments are closed ?>  <!-- If comments are closed. -->  <p class="nocomments">Comments are closed.</p>  <?php endif; ?>  <?php endif; ?>  <?php if ('open' == $post->comment_status) : ?>  <?php if ( get_option('comment_registration') && !$user_ID ) : ?>  <p>你必须 <a href="<?php echo get_option('siteurl'); ?>/wp-login.php?redirect_to=<?php the_permalink(); ?>">登录</a> 才能发表评论。</p>  <?php else : ?>  <form action="<?php echo get_option('siteurl'); ?>/wp-comments-post.php" method="post" id="commentform">    <fieldset>      <legend>看毕，沉思良久，挥键疾鼠：</legend>      <?php if ( $user_ID ) : ?>      <p>登录为 <a href="<?php echo get_option('siteurl'); ?>/wp-admin/profile.php"><?php echo $user_identity; ?></a>. <a href="<?php echo get_option('siteurl'); ?>/wp-login.php?action=logout" title="Log out of this account">退出 &raquo;</a></p>      <?php else : ?>      <p>        <input type="text" name="author" id="author" value="<?php echo $comment_author; ?>" size="22" tabindex="1" class="co id" />        <label for="author"> 江湖人称          <?php if ($req) echo "(required)"; ?>        </label>      </p>      <p>        <input type="text" name="email" id="email" value="<?php echo $comment_author_email; ?>" size="22" tabindex="2" class="co email" />        <label for="email"> 君之信址(隐)          <?php if ($req) echo "(required)"; ?>        </label>      </p>      <p>        <input type="text" name="url" id="url" value="<?php echo $comment_author_url; ?>" size="22" tabindex="3" class="co url" />        <label for="url"> ブログ</label>      </p>      <?php endif; ?>      <p id="xhtml" class="blueBorder"><small><strong>XHTML:</strong> You can use XHTML and Textile for style</small> <br />        <img onclick="grin(':!:')" alt=":!:" src="<?php bloginfo('url'); ?>/wp-includes/images/smilies/icon_exclaim.gif"/> <img onclick="grin(':roll:')" alt=":roll:" src="<?php bloginfo('url'); ?>/wp-includes/images/smilies/icon_rolleyes.gif"/> <img onclick="grin(':o')" alt=":o" src="<?php bloginfo('url'); ?>/wp-includes/images/smilies/icon_surprised.gif"/> <img onclick="grin(':lol:')" alt=":lol:" src="<?php bloginfo('url'); ?>/wp-includes/images/smilies/icon_lol.gif"/> <img onclick="grin(':P')" alt=":P" src="<?php bloginfo('url'); ?>/wp-includes/images/smilies/icon_razz.gif"/> <img onclick="grin(':(')" alt=":(" src="<?php bloginfo('url'); ?>/wp-includes/images/smilies/icon_sad.gif"/> <img onclick="grin('8O')" alt="8O" src="<?php bloginfo('url'); ?>/wp-includes/images/smilies/icon_eek.gif"/> <img onclick="grin(':x')" alt=":x" src="<?php bloginfo('url'); ?>/wp-includes/images/smilies/icon_mad.gif"/> <img onclick="grin(':oops:')" alt=":oops:" src="<?php bloginfo('url'); ?>/wp-includes/images/smilies/icon_redface.gif"/> <img onclick="grin(':)')" alt=":)" src="<?php bloginfo('url'); ?>/wp-includes/images/smilies/icon_smile.gif"/> <img onclick="grin(':?:')" alt=":?:" src="<?php bloginfo('url'); ?>/wp-includes/images/smilies/icon_question.gif"/> <img onclick="grin(':idea:')" alt=":idea:" src="<?php bloginfo('url'); ?>/wp-includes/images/smilies/icon_idea.gif"/> <img onclick="grin(':evil:')" alt=":evil:" src="<?php bloginfo('url'); ?>/wp-includes/images/smilies/icon_evil.gif"/> <img onclick="grin(';)')" alt=";)" src="<?php bloginfo('url'); ?>/wp-includes/images/smilies/icon_wink.gif"/> <img onclick="grin(':twisted:')" alt=":twisted:" src="<?php bloginfo('url'); ?>/wp-includes/images/smilies/icon_twisted.gif"/> </p>      <?php if (class_exists('emotions')) {     emotions::bar(); } ?>      <p>        <textarea name="comment" id="comment" cols="100%" rows="10" tabindex="4"></textarea>      </p>      <p>        <input name="submit" type="image" src="<?php bloginfo('template_directory'); ?>/images/submit.png" id="submit" tabindex="5" value="Submit Comment" />        <input type="hidden" name="comment_post_ID" value="<?php echo $id; ?>" />      </p>      <?php do_action('comment_form', $post->ID); ?>    </fieldset>  </form>  <?php endif; // If registration required and not logged in ?>  <?php endif; // if you delete this the sky will fall on your head ?></div>